import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Modal from "react-bootstrap/Modal";
import { toast } from "react-toastify";

import Button from "react-bootstrap/Button";
const axios = require("axios");

function Home() {
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  const [addUsershow, SetaddUserModelshow] = useState(false);
  const handleUserModelShow = (opr) => {
    setPosition(opr);
    SetaddUserModelshow(true);
    setUrl("");
  };
  const handleUserClose = () => SetaddUserModelshow(false);
  const [Url, setUrl] = useState("");
  const [Position, setPosition] = useState("");

  useEffect(() => {
    axios
      .get(process.env.REACT_APP_API_URL + "/homecontrol/getAlldata", {
        headers: { Token: token },
      })
      .then((response) => {
        for (let index = 0; index < response.data.length; index++) {
          if (Position == response.data[index].position)
            setUrl(response.data[index].url);
        }
      });
  }, []);

  const submitUserData = () => {
    axios
      .post(process.env.REACT_APP_API_URL + "/homecontrol/add", {
        url: Url.toLowerCase(),
        position: Position,
      })
      .then((res) => {
        handleUserClose();

        toast.success("Added Successfully", {});
      })
      .catch((err) => {
        if (
          err.response &&
          err.response.data &&
          err.response.data.errorMessage
        ) {
          toast.error(err.response.data.errorMessage, {});
        }
      });
  };

  return (
    <>
      <div className="card radius-10" style={{ width: "90%", margin: "auto" }}>
        <div className="row row-cols-1 row-cols-md-2 row-cols-xl-4 row-group g-0">
          <div
            style={{ borderRadius: "15px 15px 0px 0px" }}
            className="card-body cardHeader"
          >
            <h5 style={{ marginTop: "-10px" }}>Home Setup</h5>
          </div>
        </div>
        <div className="row row-cols-1 row-cols-md-2 row-cols-xl-4 row-group g-0">
          <div className="card-body" style={{ minHeight: "400px" }}>
            <div className="row">
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("channel1");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      {Url}
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("topcahnnel1");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Top Channel 1
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("todayTopclip");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Todays Top Clips
                    </h6>
                  </div>
                </div>
              </div>
            </div>

            <div className="row mt-3">
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("channel2");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Channel 2
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("topchannel2");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Top Channel 2
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("supermusic");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Super Music
                    </h6>
                  </div>
                </div>
              </div>
            </div>

            <div className="row mt-3">
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("channel3");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Channel 3
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("topchannel3");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Top Channel 3
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 "></div>
            </div>

            <div className="row mt-3">
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("channel4");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Channel 4
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("topchannel4");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Top Channel 4
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("randing1");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Trending 1
                    </h6>
                  </div>
                </div>
              </div>
            </div>

            <div className="row mt-3">
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("channel20");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Channel 5
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("explore1");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Explore 1
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("trending2");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Trending 2
                    </h6>
                  </div>
                </div>
              </div>
            </div>

            <div className="row mt-3">
              <div className="col-md-4 "></div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("explore2");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Explore 2
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("trending3");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Trending 3
                    </h6>
                  </div>
                </div>
              </div>
            </div>

            <div className="row mt-3">
              <div className="col-md-4 "></div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("explore3");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Explore 3
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("trending4");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Trending 4
                    </h6>
                  </div>
                </div>
              </div>
            </div>

            <div className="row mt-3">
              <div className="col-md-4 "></div>
              <div className="col-md-4 ">
                <div
                  onClick={() => {
                    handleUserModelShow("explore4");
                  }}
                  className=" card-body cardColumn"
                >
                  <div className="text-white">
                    <h6 style={{ marginTop: "-8px" }} className="">
                      Explore 4
                    </h6>
                  </div>
                </div>
              </div>
              <div className="col-md-4 "></div>
            </div>
          </div>
        </div>
      </div>

      <Modal show={addUsershow} onHide={handleUserClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add URL</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form className="row g-3">
            <div className="col-12">
              <label className="form-label text-white">URL</label>
              <div className="input-group" id="show_hide_password">
                <input
                  onChange={(e) => setUrl(e.target.value)}
                  type="text"
                  value={Url}
                  className="form-control border-end-0"
                  id="inputChoosePassword"
                  placeholder="Enter Username"
                />
              </div>
            </div>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleUserClose}>
            Close
          </Button>
          <Button
            type="submit"
            variant="primary"
            onClick={() => {
              submitUserData();
              // handleUserClose();
            }}
          >
            Add
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Home;
