import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Dropzone from "react-dropzone";
import "dropzone/dist/dropzone.css";

const axios = require("axios");

function Dashboard() {
  // const myDropzone = new Dropzone("#my-form");
  // const output = document.querySelector("#output");

  if (document.getElementById("my-form")) {
    var myDropzone = new Dropzone("#my-form", { url: "/file/post" });
    // other code here
  }

  const navigate = useNavigate();

  
  useEffect(() => {
    axios
      .get(process.env.REACT_APP_API_URL + "/playlist/getResImageData")
      .then((res) => {
        var userPlaylist = [];
        for (let i = 0; i < res.data.length; i++) {
          if (res.data[i].users[0] != undefined) {
            if (
              res.data[i].users[0]._id ==
              JSON.parse(sessionStorage.getItem("userDetails")).user_id
            ) {
              if (userBio == null) setUserBio(res.data[i].users[0].userbio);
              if (userImage == null) setUserImage(res.data[i].users[0].profile);
              userPlaylist.push(res.data[i]);
            }
          }
        }

        setUserPlaylist(userPlaylist);
      });
  }, []);

  const [uploadProfileLoader, setUploadProfileLoader] = useState(false);
  const uploadFile = (event) => {
    setUploadProfileLoader(true);

    const formdata = new FormData();
    formdata.append("avtar", event.target.files[0]);
    axios
      .post(
        process.env.REACT_APP_API_URL +
          "/user/profileupload?" +
          JSON.parse(sessionStorage.getItem("userDetails")).user_id,
        formdata,
        { user: JSON.parse(sessionStorage.getItem("userDetails")).user_id },
        {
          headers: {
            "Content-Type": "multipart/form-data",
            "Access-Control-Allow-Origin": "*",
          },
        }
      )
      .then((res) => {
        if (res.data.length != 0) setUploadProfileLoader(false);
        toast.success("Added Successfully", {});
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      <form>
        <div className="card radius-10 p-5">
          <h4>Image for Home</h4>
          <div className="form-group">
            <label for=""></label>
            <input
              type="file"
              className="form-control-file btn"
              name=""
              id=""
              placeholder=""
              aria-describedby="fileHelpId"
            />
            <small id="fileHelpId" className="form-text text-muted">
              Help text
            </small>
          </div>
        </div>
        <div className="card radius-10 p-5">
          <h4>Image for Playlist</h4>
          <div className="form-group">
            <label for=""></label>
            <input
              type="file"
              className="form-control-file btn"
              name=""
              id=""
              placeholder=""
              aria-describedby="fileHelpId"
            />
            <small id="fileHelpId" className="form-text text-muted">
              Help text
            </small>
          </div>
        </div>

        <button class="btn btn-dark float-end">
                Submit <span class="badge badge-secondary"></span>
        </button>
      </form>
    </>
  );
}

export default Dashboard;
