                )}
              </div>
              <div>
                <p>Image at Bottom of the Main player </p>
              </div>
            </div>

            <div
              className="col-3 transparent UserImgBox"
              item
              xs={12}
              md={4}
              style={{ position: "relative" }}
            >
              <form
                action=""
                method="post"
                encType="multipart/form-data"
                style={{ opacity: "0" }}
              >
                <input
                  onChange={(e) => uploadFile6(e)}
                  style={{
                    position: "absolute",
                    top: "0",
                    opacity: "0",
                    height: "100%",
                  }}
                  type="file"
                  name=""
                  id="input_file"
                  accept=".jpg,.jpeg,.png"
                  className="form-control"
                  value=""
                />
              </form>
              <div className="UserImg">
                {allData[6] ? (
                  <div>
                    {allData.length != 0 &&
                    allData[6].resimage &&
                    allData[6].imagelocation == 2 ? (
                      <img
                        id="proFileImage"
                        src={require("../../images/reserved/" +
                          allData[6].resimage)}
                        className="img-fluid"
                        alt=""
                        style={{ height: "120px" }}
                      />
                    ) : (
                      <img
                        id="proFileImage"
                        src={require("../../images/default.jpg")}
                        className="img-fluid"
                        alt=""
                        style={{ height: "120px" }}
                      />
                    )}
                  </div>
                ) : (
                  <img
                    id="proFileImage"
                    src={require("../../images/default.jpg")}
                    className="img-fluid"
                    alt=""
                    style={{ height: "120px" }}
                  />
                )}
              </div>
              <div>
                <p>First Image of Local Section </p>
              </div>
            </div>
          </div>
        </div>
      ) : (
        ""
      )}
      {/* End Playlist Section */}
    </>
  );
}

export default SpaceResImages;
