import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DataTable from "react-data-table-component";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";



const axios = require("axios");

function Dashboard() {
  const navigate = useNavigate();

  const [showAllPlaylist, SetshowAllPlaylist] = useState(false);
  const [allPlaylistData, setallPlaylistData] = useState([]);


  const handleChange = (state) => {
    setDeleteId(state.selectedRows);
  };

  const columns = [
    {
      id: 2,
      name: "USER",
      selector: (row) => row.users,
      sortable: true,
      reorder: true,
    },
    {
      id: 3,
      name: "TITLE",
      selector: (row) => <>{row.videos[0] ? (<img src={row.videos[0].thumbnail} alt="Not Found" width={200}/>) : ("")}</>,
      sortable: true,
      reorder: true,
    },
    {
      id: 4,
      name: "PLAYS / LIKES",
      selector: (row) => <>{row.videos[0] ? (row.videos[0].views)+"/"+(row.videos[0].likes) : ("")}</>,
      sortable: true,
      reorder: true,
    },
    {
      id: 5,
      name: "LENGTH",
      selector: (row) => <>04:23:00</>,
      sortable: true,
      reorder: true,
    },
    {
      id: 6,
      name: "URL",
      selector: (row) => (
        <>
          <div className="row">
            <div className="col">
              <textarea cols="21" rows="6">
                {"http://190.92.153.226:3000/playlist?id=" + row._id}
              </textarea>
            </div>
          </div>
        </>
      ),
      sortable: true,
      reorder: true,
    },
    {
      id: 7,
      name: "",
      cell: (row) => (
        <>
          <div className="row">
            <div className="col mb-2">
              <button
                className="btn backgroundButton border-white"
                style={{ width: "140px" }}
              >
                <i style={{ color: "white", fontSize: "1rem" }}>CSV REPLACE</i>
              </button>
            </div>
            <input
              style={{
                opacity: "0",
                zIndex: "1",
                position: "absolute",
                width: "86%",
              }}
              accept=".csv"
              onChange={(e) => {
                uploadFile(e);
                setUpdateId(row._id);
              }}
              type="file"
              className="form-control"
            />
            <div className="col mb-2">
              <button
                className="btn backgroundButton border-white"
                style={{ width: "140px" }}
              >
                <i style={{ color: "white", fontSize: "1rem" }}>RANDMOIZE</i>
              </button>
            </div>
          </div>
        </>
      ),
    },
  ];

  return (
    <div className="card radius-10" style={{ width: "90%", margin: "auto" }}>
      <div className="row row-cols-1 row-cols-md-2 row-cols-xl-4 row-group g-0">
        <div
          style={{ borderRadius: "15px 15px 0px 0px" }}
          className="card-body cardHeader"
        >
          <h5 style={{ marginTop: "-10px" }}>Manage Users</h5>
        </div>
      </div>
      <div className="row row-cols-1 row-cols-md-2 row-cols-xl-4 row-group g-0">
        <div className="card-body" style={{ height: "400px" }}>
          <div className="row">
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    SEARCH USERS
                  </h6>
                </div>
              </div>
            </div>
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    DELETE SELECTED USERS
                  </h6>
                </div>
              </div>
            </div>
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    SHOW ALL USERS
                  </h6>
                </div>
              </div>
            </div>
          </div>

          <div className={showAllPlaylist ? "row mt-3 d-none" : "row mt-5 "}>
            <DataTable
              style={{ background: "transparent" }}
              title=""
              columns={columns}
              data={allPlaylistData}
              defaultSortFieldId={1}
              sortIcon={<ArrowUpwardIcon />}
              pagination
              selectableRows
              onSelectedRowsChange={handleChange}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
