import React, { useState, useEffect } from "react";
import "../../css/style.css";
import { toast } from "react-toastify";

import swal from "sweetalert";

const axios = require("axios");
toast.configure();

function SpaceResImages() {
  const token = localStorage.getItem("token");
  // console.log(token);
  // Featch All Data
  const [allData, setallData] = useState([]);

  useEffect(() => {
    callData();
  }, []);

  function callData() {
    axios
      .get(process.env.REACT_APP_API_URL + "/user/getAlldata", {
        headers: { Token: token },
      })
      .then((response) => {
        for (let index = 0; index < response.data.length; index++) {
          response.data[index].id = index + 1;
        }
        setallData(response.data);
      });
  }

  window.addEventListener("ready", (event) => {
    callData();
  });

  //  Delete PlayList
  const onDelete = (val) => {
    swal({
      title: "Are you sure?",
      text: "Once deleted, you will not be able to recover this imaginary file!",
      icon: "warning",
      buttons: true,
      dangerMode: true,
    }).then((willDelete) => {
      if (willDelete) {
        axios
          .post(process.env.REACT_APP_API_URL + "/user/delete", { id: val })
          .then((res) => {
            toast.success("Delete Successfully", {});
            callData();
          })
          .catch((err) => {
            if (
              err.response &&
              err.response.data &&
              err.response.data.errorMessage
            ) {
              toast.error(err.response.data.errorMessage, {});
            }
          });
      }
    });
  };
  //End Delete users

  const columns = [
    {
      id: 1,
      name: "S.no",
      selector: (row) => row.id,
      sortable: true,
      reorder: true,
    },
    {
      id: 2,
      name: "Email / Phone",
      selector: (row) => row.email,
      sortable: true,
      reorder: true,
    },
    {
      id: 3,
      name: "Username",
      selector: (row) => row.username,
      sortable: true,
      reorder: true,
    },

    {
      id: 4,
      name: "User Type",
      selector: (row) => (row.role == 1 ? "Admin" : "User"),
      sortable: true,
      right: true,
      reorder: true,
    },
    {
      id: 5,
      name: "",
      // selector: (row) => row.role,
      sortable: true,
      right: true,
      reorder: true,
    },
    {
      id: 6,
      name: "Action",
      button: true,
      cell: (row) => (
        <React.Fragment>
          <button
            className="btn btn-sm btn-light"
            onClick={() => onDelete(row._id)}
          >
            <i className="bx bx-trash me-0"></i>
          </button>
          &nbsp;
          <button
            onClick={() => {
              setEmail(row.email);
              setUserName(row.username);
              setRole(row.role);
              setUpdateId(row._id);
              handleUserModelShow("update");
            }}
            className="btn btn-sm btn-light"
          >
            <i className="lni lni-pencil-alt me-0"></i>
          </button>
        </React.Fragment>
      ),
    },
    {
      id: 6,
      name: "",
      // selector: (row) => row.role,
      sortable: true,
      right: true,
      reorder: true,
    },
  ];

  const [addUsershow, SetaddUserModelshow] = useState(false);
  const handleUserModelShow = (opr) => {
    SetaddUserModelshow(true);
    setOpration(opr);
    if (opr === "add") {
      setUserName("");
      setEmail("");
      setPassword("");
      setRole("");
    }
  };
  const handleUserClose = () => SetaddUserModelshow(false);

  const [Passwordshow, SetPasswordModelshow] = useState(false);
  const handlePasswordModelShow = (opr) => {
    SetPasswordModelshow(true);
    setOpration(opr);
    if (opr === "add") {
      setUserName("");
      setEmail("");
      setPassword("");
      setConfirmPassword("");
      setRole("");
    }
  };
  const handlePasswordClose = () => SetPasswordModelshow(false);

  //Verify User
  const verifyUser = (id) => {
    var data = id;
    axios
      .post(process.env.REACT_APP_API_URL + "/user/verify", {
        id: data,
      })
      .then((res) => {
        toast.success("Email Account Verify Successfully");
      })
      .catch((err) => {
        toast.error("Email Account Not verify");
      });
  };

  // End Verify User

  // ////////////////////////
  // Submit Users Data
  const [opration, setOpration] = useState("");
  const [UserName, setUserName] = useState("");
  const [Password, setPassword] = useState("");
  const [ConfirmPassword, setConfirmPassword] = useState("");
  const [Role, setRole] = useState("");
  const [Email, setEmail] = useState("");
  // const [user, setuser] = useState("");
  const [updateUserId, setUpdateId] = useState("");
  const submitUserData = () => {
    axios
      .post(process.env.REACT_APP_API_URL + "/user/add", {
        username: UserName.toLowerCase(),
        email: Email.toLowerCase(),
        password: Password,
        role: Role,
        opration: opration,
        id: updateUserId,
      })
      .then((res) => {
        handleUserClose();
        callData();
        if (opration !== "update") toast.success("Added Successfully", {});
        else toast.success("Update Successfully", {});
      })
      .catch((err) => {
        if (
          err.response &&
          err.response.data &&
          err.response.data.errorMessage
        ) {
          toast.error(err.response.data.errorMessage, {});
        }
      });
    callData();
  };

  const submitPasswordData = () => {
    axios
      .post(process.env.REACT_APP_API_URL + "/user/update_password", {
        password: Password,
        confirmPassword: ConfirmPassword,
        opration: opration,
        id: updateUserId,
      })
      .then((res) => {
        callData();
        if (opration !== "changePassword")
          toast.success("Added Successfully", {});
        else toast.success("Update Successfully", {});
      })
      .catch((err) => {
        if (
          err.response &&
          err.response.data &&
          err.response.data.errorMessage
        ) {
          toast.error(err.response.data.errorMessage, {});
        }
      });
  };

  return (
    <>
      <div className="row">
        <div className="col-3">
          <label className="form-label text-white">Role</label>
          <div className="input-group" id="show_hide_password">
            <select
              className="form-control"
              onChange={(e) => setRole(e.target.value)}
              required
            >
              <option selected disabled>
                -----Select Page----
              </option>
              <option value={1}>All</option>
              <option value={2}>Home</option>
              <option value={2}>Playlist</option>
              <option value={2}>Profile</option>
            </select>
          </div>
        </div>
      </div>
      <br />
      <br />
      <div className="row">
        <div
          className="col-3 transparent UserImgBox"
          item
          xs={12}
          md={4}
          style={{ position: "relative" }}
        >
          <form
            action=""
            method="post"
            enctype="multipart/form-data"
            style={{ opacity: "1" }}
          >
            <input
              // onChange={(e) => uploadFile(e)}
              style={{ position: "absolute", top: "0", opacity: "0" }}
              type="file"
              name=""
              id="input_file"
              accept=".jpg,.jpeg,.png"
              class="form-control"
              value=""
            />
          </form>
          <div className="UserImg">
            <img
              id="proFileImage"
              src={require("../../images/default.jpg")}
              className="img-fluid"
              alt=""
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default SpaceResImages;
