import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DataTable from "react-data-table-component";

const axios = require("axios");

function Dashboard() {
  const navigate = useNavigate();
  const [allData, setallData] = useState([]);

  useEffect(() => {
    axios
      .get(process.env.REACT_APP_API_URL + "/manageplaylist/getAlldata")
      .then((response) => {
        for (let index = 0; index < response.data.length; index++)
          response.data[index].id = index + 1;
        setallData(response.data);
      });
  }, []);
  const columns = [
    {
      id: 1,
      name: "S.no",
      selector: (row) => row.id,
      sortable: true,
      reorder: true,
    },
    {
      id: 2,
      name: "Title",
      selector: (row) => row.title,
      sortable: true,
      reorder: true,
    },
    { id: 3, name: "Videos", button: true, cell: (row) => <i>Active</i> },
    // { id: 4, name: "", cell: (row) => "" },
    {
      id: 4,
      name: "Action",
      button: true,
      cell: (row) => (
        <React.Fragment>
          <Button
            onClick={() => deletePlayList(row._id)}
            className="btn btn-sm btn-light"
          >
            <i className="bx bx-trash me-0"></i>
          </Button>
          &nbsp;
          <Button
            onClick={() => {
              setuser(row.user);
              setPlayListName(row.title);
              setUpdateId(row._id);
              handlePlaylistModelShow("update");
            }}
            className="btn btn-sm btn-light"
          >
            <i className="lni lni-pencil-alt me-0"></i>
          </Button>
          &nbsp;
        </React.Fragment>
      ),
    },
    {
      id: 5,
      name: "",
      cell: (row) => (
        <>
          <Button className="btn btn-sm btn-light">
            <i className="lni lni-cloud-upload me-0"></i>
          </Button>
          <input
            style={{
              opacity: "0",
              zIndex: "1",
              position: "absolute",
              width: "40px",
            }}
            accept=".csv"
            onChange={(e) => {
              uploadFile(e);
              setUpdateId(row._id);
            }}
            type="file"
            className="form-control"
          />
        </>
      ),
    },
  ];
  return (
    <div className="card radius-10" style={{ width: "90%", margin: "auto" }}>
      <div className="row row-cols-1 row-cols-md-2 row-cols-xl-4 row-group g-0">
        <div
          style={{ borderRadius: "15px 15px 0px 0px" }}
          className="card-body cardHeader"
        >
          <h5 style={{ marginTop: "-10px" }}>Manage Playlists</h5>
        </div>
      </div>
      <div className="row row-cols-1 row-cols-md-2 row-cols-xl-4 row-group g-0">
        <div className="card-body" style={{ height: "400px" }}>
          <div className="row">
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    Search Playlist
                  </h6>
                </div>
              </div>
            </div>
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    Sort By Date, User or A-Z
                  </h6>
                </div>
              </div>
            </div>
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    Manage Playlist URL
                  </h6>
                </div>
              </div>
            </div>
          </div>

          <div className="row mt-3">
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    Delete User Playlist
                  </h6>
                </div>
              </div>
            </div>
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    CSV playlist upload/user assign
                  </h6>
                </div>
              </div>
            </div>
            <div className="col-md-4 ">
              <div className=" card-body cardColumn">
                <div className="text-white">
                  <h6 style={{ marginTop: "-8px" }} className="">
                    List Of Most Viewed Playlists
                  </h6>
                </div>
              </div>
            </div>
          </div>

          <div className="row mt-3">
            <DataTable
              style={{ background: "transparent" }}
              title="Playlists"
              columns={columns}
              data={allData}
              defaultSortFieldId={1}
              sortIcon={<ArrowUpwardIcon />}
              pagination
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
